import React, { useState, useEffect } from 'react';
import CotizacionForm from '../components/CotizacionForm';
import { db } from '../firebaseConfig';

const cotizaciones = () => {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    // Aquí puedes obtener los datos de los clientes desde tu base de datos o cualquier otro lugar
    // Por ejemplo, puedes obtener los clientes de Firebase Firestore
    const fetchClientes = async () => {
      // Aquí deberías implementar la lógica para obtener los clientes desde tu base de datos
      // En este ejemplo, supondré que tienes una colección llamada 'clientes' en Firestore
      const querySnapshot = await db.collection('clientes').get();
      const clientesData = querySnapshot.docs.map(doc => doc.data());
      setClientes(clientesData);
    };

    fetchClientes();
  }, []);

  return (
    <div>
      {/* Pasamos los datos de los clientes al componente CotizacionForm */}
      <CotizacionForm clientes={clientes} />
    </div>
  );
};

export default cotizaciones;
