import React from 'react';

function PreviaCotizacion({ cotizacion, clientes, cerrarPrevia }) {
  const { cliente, asunto, fechaCotizacion, productosSeleccionados } = cotizacion;

  // Busca el cliente correspondiente en la lista de clientes
  const clienteEncontrado = clientes.find(cliente => cliente.id === cotizacion.cliente);
  const nombreCliente = clienteEncontrado ? clienteEncontrado.empresa : '';

  return (
    <div className="previa-cotizacion">
      <h1>Previa</h1>
      <hr />
      <p>Fecha de cotización: {fechaCotizacion}</p>
      <p>Asunto: {asunto}</p>
      <p>Cliente: {nombreCliente}</p> {/* Muestra el nombre de la empresa del cliente */}
      <h3>Descripción</h3>
      <table className="productos-table">
        <thead>
          <tr>
            <th>Cantidad</th>
            <th>ID</th>
            <th>Descripción</th>
            <th>Precio</th>
            <th>Subtotal</th>
          </tr>
        </thead>
        <tbody>
          {productosSeleccionados.map((producto) => (
            <tr key={producto.id}>
              <td>{producto.cantidad}</td>
              <td>{producto.productoIdEditado}</td>
              <td>{producto.nombre}</td>
              <td>${parseFloat(producto.costo).toFixed(2)}</td>
              <td>${parseFloat(producto.subtotal).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={cerrarPrevia}>Cerrar</button>
    </div>
  );
}

export default PreviaCotizacion;
