// cotizacion.js
import React, { useState } from 'react';
import CotizacionForm from '../components/CotizacionForm';
import TablaCotizaciones from '../components/TablaCotizaciones';

const Cotizaciones = () => {
  const [cotizaciones, setCotizaciones] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(true);

  const handleGuardarCotizacion = (nuevaCotizacion) => {
    setCotizaciones([...cotizaciones, nuevaCotizacion]);
    setMostrarFormulario(false); // Ocultar el formulario después de guardar
  };

  return (
    <div>
      {mostrarFormulario && <CotizacionForm setCotizaciones={handleGuardarCotizacion} />}
      <TablaCotizaciones cotizaciones={cotizaciones} />
    </div>
  );
}

export default Cotizaciones;
