import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClipboardList, faFileInvoice, faUsers, faUser, faClipboard, faChartBar, faFileAlt } from '@fortawesome/free-solid-svg-icons';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      <div className="sidebar-header">
        <img className="menu-icon" src="/img/menu.svg" alt="Menu Icon" onClick={toggleSidebar} />
      </div>
      <div className="sidebar-content">
        <a className="centrar-logo" href="/">
          <img className="logo" src="/img/logo-iciamex.png" alt="ICIAMEX logo" />
        </a>
        <nav id="primary-menu" className="navbar">
          <ul className="nav navbar-nav">
          <FontAwesomeIcon icon={faClipboardList} /><li> <a className="active" href="index.html">Ordenes de Compra</a></li>
            <li><FontAwesomeIcon icon={faFileInvoice} /> <a href="/">Cotizaciones</a></li>
            <li><FontAwesomeIcon icon={faFileAlt} /> <a href="/">Facturas</a></li>
            <li><FontAwesomeIcon icon={faUsers} /> <a href="/">Lista de Proveedores</a></li>
            <li><FontAwesomeIcon icon={faUser} /> <a href="/">Lista de Clientes</a></li>
            <li><FontAwesomeIcon icon={faClipboard} /> <a href="/">Lista de Artículos</a></li>
            <li><FontAwesomeIcon icon={faChartBar} /> <a href="/">Informes</a></li>
          </ul>
        </nav>
      </div>
      <div className="sidebar-footer">
        <div className="sidebar-copyright">&copy; 2024 ICIAMEX</div>
      </div>
    </div>
  );
};

export default Sidebar;
