import React, { useState } from 'react';
import { collection, addDoc } from 'firebase/firestore';
import { db } from '../firebaseConfig';

const AddClientForm = () => {
    const [formData, setFormData] = useState({
        id: '',
        empresa: '',
        rfc: '',
        regimenFiscal: '',
        moneda: '',
        telefono: '',
        correo: '',
        imagenURL: null, // Cambiar el valor inicial a null
        domicilio: '',
        numeroExt: '',
        numeroInt: '',
        colonia: '',
        codigoPostal: '',
        ciudad: '',
        estado: ''
    });

    const [step, setStep] = useState(1); // Estado para controlar el paso del formulario

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (step === 1) {
                // Si es el primer paso, avanzar al siguiente paso
                setStep(step => step + 1);
            } else if (step === 2) {
                // Si es el segundo paso, enviar los datos a Firestore
                const clientData = {
                    ...formData
                };
                await addDoc(collection(db, 'clientes'), clientData);
                console.log("Cliente registrado con éxito");
                // Reiniciar el formulario después de enviar los datos
                setFormData({
                    id: '',
                    empresa: '',
                    rfc: '',
                    regimenFiscal: '',
                    moneda: '',
                    telefono: '',
                    correo: '',
                    imagenURL: null,
                    domicilio: '',
                    numeroExt: '',
                    numeroInt: '',
                    colonia: '',
                    codigoPostal: '',
                    ciudad: '',
                    estado: ''
                });
            }
        } catch (error) {
            console.error("Error al agregar cliente: ", error);
        }
    };

    return (
        <div>
            {step === 1 && (
                <form onSubmit={handleSubmit} className="client-form">
                    <input type="text" name="id" placeholder="ID" value={formData.id} onChange={handleChange} />
                    <input type="text" name="empresa" placeholder="Nombre de la empresa" value={formData.empresa} onChange={handleChange} />
                    <input type="text" name="rfc" placeholder="RFC" value={formData.rfc} onChange={handleChange} />
                    {/* Otros campos del primer paso */}
                    <button type="submit">Siguiente</button>
                </form>
            )}
            {step === 2 && (
                <form onSubmit={handleSubmit} className="client-form">
                    <input type="text" name="domicilio" placeholder="Domicilio" value={formData.domicilio} onChange={handleChange} />
                    <input type="text" name="numeroExt" placeholder="No. Ext" value={formData.numeroExt} onChange={handleChange} />
                    {/* Otros campos del segundo paso */}
                    <button type="submit">Agregar Cliente</button>
                </form>
            )}
        </div>
    );
};

export default AddClientForm;
