import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db } from '../firebaseConfig';
import Swal from 'sweetalert2';
import Modal from 'react-modal';
import ClientsTable from './ClientsTable';

const ListaClientes = () => {
    const [clientes, setClientes] = useState([]);
    const [formData, setFormData] = useState({
        empresa: '',
        rfc: '',
        regimenFiscal: '',
        moneda: '',
        telefono: '',
        correo: '',
        imagenURL: null,
        domicilio: '',
        numeroExt: '',
        numeroInt: '',
        colonia: '',
        codigoPostal: '',
        ciudad: '',
        estado: ''
    });
    const [modalIsOpen, setModalIsOpen] = useState(false); // Estado para controlar la apertura/cierre del modal

    useEffect(() => {
        obtenerClientes();
    }, []);

    const obtenerClientes = async () => {
        const clientesSnapshot = await getDocs(collection(db, 'clientes'));
        const listaClientes = clientesSnapshot.docs.map(doc => doc.data());
        setClientes(listaClientes);
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (formData.empresa.trim() === '') {
            Swal.fire({
                icon: 'error',
                title: 'Oops...',
                text: 'El campo "Nombre de la Empresa" es obligatorio!'
            });
            return;
        }
        try {
            const clienteData = { ...formData };
            await addDoc(collection(db, 'clientes'), clienteData);
            Swal.fire({
                icon: 'success',
                title: '¡Cliente registrado con éxito!',
                showConfirmButton: false,
                timer: 1500
            }).then(() => {
                setFormData({
                    empresa: '',
                    rfc: '',
                    regimenFiscal: '',
                    moneda: '',
                    telefono: '',
                    correo: '',
                    imagenURL: null,
                    domicilio: '',
                    numeroExt: '',
                    numeroInt: '',
                    colonia: '',
                    codigoPostal: '',
                    ciudad: '',
                    estado: ''
                });
                obtenerClientes(); // Actualizar la lista de clientes después de agregar uno nuevo
            });
        } catch (error) {
            console.error("Error al agregar cliente:", error);
        }
    };

    return (
        <div>
            <h1>Lista de Clientes</h1>
            <button onClick={() => setModalIsOpen(true)}>+ Nuevo</button>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={() => setModalIsOpen(false)}
                ariaHideApp={false} // Deshabilitar la advertencia de accesibilidad
            >
                <h2>Nuevo Cliente</h2>
                <form onSubmit={handleSubmit} className="client-form">
                    <input type="text" name="empresa" placeholder="Nombre de la Empresa" value={formData.empresa} onChange={handleChange} />
                    <input type="text" name="rfc" placeholder="RFC" value={formData.rfc} onChange={handleChange} />
                    <input type="text" name="regimenFiscal" placeholder="Régimen Fiscal" value={formData.regimenFiscal} onChange={handleChange} />
                    <input type="text" name="moneda" placeholder="Moneda" value={formData.moneda} onChange={handleChange} />
                    <input type="text" name="telefono" placeholder="Teléfono" value={formData.telefono} onChange={handleChange} />
                    <input type="email" name="correo" placeholder="Correo Electrónico" value={formData.correo} onChange={handleChange} />
                    <input type="file" name="imagen" onChange={handleChange} />
                    <button type="submit">Agregar Cliente</button>
                </form>
            </Modal>
            <ClientsTable clientes={clientes} />
        </div>
    );
};

export default ListaClientes;
