import React, { useState } from 'react';
import Modal from 'react-modal';


const ProductButton = ({ products, setProducts }) => {
  const [percentage, setPercentage] = useState(10);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const updatePrices = (increase) => {
    const adjustFactor = 1 + (increase ? percentage : -percentage) / 100;
    const adjustedProducts = products.map(product => ({
      ...product,
      costo: product.costo * adjustFactor,
    }));
    setProducts(adjustedProducts);
    setModalIsOpen(false); // Cerrar el modal después de ajustar los precios
  };

  const handlePercentageChange = (e) => {
    setPercentage(parseFloat(e.target.value) || 0);
  };

  return (
    <div className="product-button-container">
      <h2>Ajuste de Precios</h2>
      <button className="modal-button" onClick={() => setModalIsOpen(true)}>Ajustar precios</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        className="modal"
        overlayClassName="modal-overlay"
      >
        <label>
          Porcentaje:
          <input type="number" value={percentage} onChange={handlePercentageChange} />
        </label>
        <button onClick={() => updatePrices(true)}>Aumentar precios</button>
        <button onClick={() => updatePrices(false)}>Disminuir precios</button>
      </Modal>
    </div>
  );
};

export default ProductButton;
