import React, { useState, useEffect } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from '../firebaseConfig';

function CotizacionForm() {
  const [cliente, setCliente] = useState('');
  const [clientes, setClientes] = useState([]);
  const [productos, setProductos] = useState([]);
  const [asunto, setAsunto] = useState('');
  const [fechaCotizacion, setFechaCotizacion] = useState(getCurrentDate());
  const [fechaVencimiento, setFechaVencimiento] = useState('');
  const [productoSeleccionado, setProductoSeleccionado] = useState('');
  const [productosSeleccionados, setProductosSeleccionados] = useState([]);
  const [mostrarPrevia, setMostrarPrevia] = useState(false);

  useEffect(() => {
    obtenerClientes();
    obtenerProductos();
  }, []);

  const obtenerClientes = async () => {
    try {
      const clientesSnapshot = await getDocs(collection(db, 'clientes'));
      const listaClientes = clientesSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setClientes(listaClientes);
    } catch (error) {
      console.error('Error al obtener clientes:', error);
    }
  };

  const obtenerProductos = async () => {
    try {
      const productosSnapshot = await getDocs(collection(db, 'productos'));
      const listaProductos = productosSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setProductos(listaProductos);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
    }
  };

  function getCurrentDate() {
    const date = new Date();
    return date.toISOString().split('T')[0];
  }

  function agregarProducto() {
    if (productoSeleccionado) {
      const producto = productos.find(p => p.id === productoSeleccionado);
      if (producto) {
        setProductosSeleccionados([...productosSeleccionados, producto]);
        setProductoSeleccionado('');
      }
    }
  }

  const eliminarProducto = (idProducto) => {
    setProductosSeleccionados(prevProductos => prevProductos.filter(producto => producto.id !== idProducto));
  };

  const actualizarCantidad = (idProducto, cantidad) => {
    setProductosSeleccionados(prevProductos =>
      prevProductos.map(producto =>
        producto.id === idProducto ? { ...producto, cantidad: cantidad !== '' ? Number(cantidad) : 0 } : producto
      )
    );
  };

  const mostrarPreviaCotizacion = () => {
    setMostrarPrevia(true);
  };

  return (
    <div className="cotizacion-form">
      <form>
        {/* AquÃ­ va el contenido del formulario */}
        <button type="button" onClick={mostrarPreviaCotizacion}>Mostrar Previa de CotizaciÃ³n</button>
      </form>

      {/* Previa de la cotizaciÃ³n */}
      {mostrarPrevia && (
        <PreviaCotizacion
          cliente={cliente}
          asunto={asunto}
          fechaCotizacion={fechaCotizacion}
          productosSeleccionados={productosSeleccionados}
          eliminarProducto={eliminarProducto}
          actualizarCantidad={actualizarCantidad}
        />
      )}
    </div>
  );
}

function PreviaCotizacion({ cliente, asunto, fechaCotizacion, productosSeleccionados, eliminarProducto, actualizarCantidad }) {
  // FunciÃ³n para calcular los totales
  const calcularTotales = () => {
    let subtotal = 0;
    productosSeleccionados.forEach(producto => {
      subtotal += producto.cantidad * producto.costo;
    });

    const iva = subtotal * 0.16;
    const total = subtotal + iva;

    return { subtotal, iva, total };
  };

  const { subtotal, iva, total } = calcularTotales();

  return (
    <div className="previa-cotizacion">
      <h1>Previa</h1>
      <h2>Factura: 0001</h2>
      <hr />
      <p>Fecha de cotizaciÃ³n: {fechaCotizacion}</p>
      <p>Asunto: {asunto}</p>
      <p>Cliente: {cliente}</p>
      <h3>DESCRIPCIÃN</h3>
      {/* AquÃ­ puedes agregar los productos seleccionados */}
      <h3>Subtotal: ${subtotal.toFixed(2)}</h3>
      <h3>IVA: ${iva.toFixed(2)}</h3>
      <h3>Total: ${total.toFixed(2)}</h3>
      <p>Descuento %</p>
    </div>
  );
}

export default CotizacionForm;
