// ProductButton.js
import React, { useState } from 'react';

const ProductButton = ({ products, setProducts }) => {
  const [percentage, setPercentage] = useState(0); // Inicializamos el porcentaje en 0

  const handlePercentageChange = (e) => {
    const newPercentage = parseFloat(e.target.value);
    setPercentage(newPercentage);
    // Actualizamos los precios de los productos al cambiar el porcentaje
    updatePrices(newPercentage);
  };

  const updatePrices = (newPercentage) => {
    if (!products || !products.length || !setProducts) return;

    const updatedProducts = products.map(product => {
      const adjustedCost = product.cost * (1 + newPercentage / 100);
      return { ...product, cost: adjustedCost };
    });

    setProducts(updatedProducts);
  };

  return (
    <div>
      <h2>Bot√≥n de Ajuste de Precios</h2>
      <div>
        <label>
          Porcentaje de ajuste (%):
          <input type="number" value={percentage} onChange={handlePercentageChange} />
        </label>
      </div>
    </div>
  );
};

export default ProductButton;
