import React, { useState, useEffect } from 'react';
import CotizacionForm from '../components/CotizacionForm';
import { db } from '../firebaseConfig';

const Cotizacion = () => {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    // Función asincrónica para obtener los clientes de la base de datos
    const fetchClientes = async () => {
      try {
        // Obtener la colección de clientes desde la base de datos
        const clientesSnapshot = await db.collection('clientes').get();
        // Mapear los documentos de la colección a datos de clientes
        const clientesData = clientesSnapshot.docs.map(doc => doc.data());
        // Establecer los datos de los clientes en el estado
        setClientes(clientesData);
      } catch (error) {
        console.error('Error al obtener clientes:', error);
      }
    };

    // Llamar a la función para obtener los clientes cuando el componente se monta
    fetchClientes();
  }, []); // El array vacío como segundo argumento asegura que el efecto se ejecute solo una vez al montar el componente

  return (
    <div>
      {/* Pasar los datos de los clientes al componente CotizacionForm */}
      <CotizacionForm clientes={clientes} />
    </div>
  );
};

export default Cotizacion;
