import React, { useState } from 'react';

const ProductButton = ({ products, setProducts }) => {
  // Defino un 10% como el porcentaje de ajuste predeterminado
  const [percentage, setPercentage] = useState(10); 

  const updatePrices = (direction) => {
    if (!products || !products.length || !setProducts) return;

    const adjustFactor = direction === 'increase' ? (1 + percentage / 100) : (1 - percentage / 100);
    
    const adjustedProducts = products.map(product => {
      const adjustedCost = product.cost * adjustFactor;
      return { ...product, cost: adjustedCost };
    });

    setProducts(adjustedProducts);
  };

  const handlePercentageChange = (e) => {
    const newPercentage = parseFloat(e.target.value) || 0;
    setPercentage(newPercentage);
  };

  return (
    <div>
      <h2>Bot√≥n de Ajuste de Precios</h2>
      <div>
        <label>
          Porcentaje de ajuste (%):
          <input type="number" value={percentage} onChange={handlePercentageChange} min="0" />
        </label>
      </div>
      <div>
        <button onClick={() => updatePrices('increase')}>Aumentar precios</button>
        <button onClick={() => updatePrices('decrease')}>Disminuir precios</button>
      </div>
    </div>
  );
};

export default ProductButton;
