import React, { useState } from 'react';
import './Sidebar.css'; // Asegúrate de importar el CSS correctamente

const Sidebar = () => {
  // Estados para manejar los toggles
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div id="nav-bar" className={`sidebar ${isOpen ? 'open' : ''}`}>
      <div id="nav-header" className="sidebar-header">
        <a id="nav-title" href="https://codepen.io" target="_blank" rel="noopener noreferrer">
          CODEPEN
        </a>
        <label htmlFor="nav-toggle">
          <span id="nav-toggle-burger" className="menu-icon" onClick={toggleSidebar}></span>
        </label>
        <hr />
      </div>
      <div className="sidebar-content">
        <a className="centrar-logo" href="/">
          <img className="logo" src="/img/logo-iciamex.png" alt="ICIAMEX logo" />
        </a>
        <nav id="primary-menu" className="navbar">
          <ul className="nav navbar-nav">
            <li><a className="active" href="index.html">Ordenes de Compra</a></li>
            <li><a href="/">Cotizaciones</a></li>
            <li><a href="/">Facturas</a></li>
            <li><a href="/">Lista de Proveedores</a></li>
            <li><a href="/">Lista de Clientes</a></li>
            <li><a href="/">Lista de Artículos</a></li>
            <li><a href="/">Informes</a></li>
          </ul>
        </nav>
      </div>
      <div className="sidebar-footer">
        <div className="sidebar-copyright">&copy; 2024 ICIAMEX</div>
      </div>
    </div>
  );
};

export default Sidebar;
