import React, { useState, useEffect } from 'react';
import { collection, getDocs, addDoc } from 'firebase/firestore';
import { db } from '../firebaseConfig';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import CotizacionForm from '../components/CotizacionForm';
import TablaCotizaciones from '../components/TablaCotizaciones';


const MySwal = withReactContent(Swal);

function CotizacionesContainer() {
  const [cotizaciones, setCotizaciones] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(true);
  const [mostrarPrevia, setMostrarPrevia] = useState(false); // Agrega el estado para controlar la visibilidad de la previa
  const [cotizacionSeleccionada, setCotizacionSeleccionada] = useState(null); // Agrega el estado para almacenar la cotización seleccionada

  useEffect(() => {
    obtenerCotizaciones();
  }, []);

  const obtenerCotizaciones = async () => {
    try {
      const cotizacionesSnapshot = await getDocs(collection(db, 'cotizaciones'));
      const listaCotizaciones = cotizacionesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCotizaciones(listaCotizaciones);
    } catch (error) {
      console.error('Error al obtener las cotizaciones:', error);
    }
  };

  const guardarCotizacion = async (cotizacionData) => {
    try {
      if (!cotizacionData.cliente || !cotizacionData.asunto || !cotizacionData.fechaVencimiento || cotizacionData.productosSeleccionados.length === 0) {
        MySwal.fire({
          icon: 'error',
          title: 'Oops...',
          text: 'Todos los campos son obligatorios',
        });
        return;
      }

      const docRef = await addDoc(collection(db, 'cotizaciones'), cotizacionData);
      console.log('Cotización guardada con ID: ', docRef.id);
      obtenerCotizaciones();
      setMostrarFormulario(false);
    } catch (error) {
      console.error('Error al guardar la cotización:', error);
    }
  };

  const verPrevia = (cotizacion) => {
    setCotizacionSeleccionada(cotizacion);
    setMostrarPrevia(true);
  };

  return (
    <div>
      {mostrarFormulario ? (
        <CotizacionForm guardarCotizacion={guardarCotizacion} />
      ) : mostrarPrevia ? ( // Mostrar la previa si mostrarPrevia es true
        <PreviaCotizacion cotizacion={cotizacionSeleccionada} cerrarPrevia={() => setMostrarPrevia(false)} />
      ) : (
        <TablaCotizaciones cotizaciones={cotizaciones} verPrevia={verPrevia} /> // Pasar la función verPrevia al componente TablaCotizaciones
      )}
    </div>
  );
}

export default CotizacionesContainer;
