import React, { useState } from 'react';

const ProductButton = ({ products, setProducts }) => {
  const [percentage, setPercentage] = useState(100); // Porcentaje de aumento por defecto

  const increasePrices = () => {
    if (!products || !products.length || !setProducts) return;

    const newProducts = products.map(product => {
      // Asegurarse de que el costo incrementado sea un número válido.
      const increasedCost = product.cost * (1 + percentage / 100);
      return { ...product, cost: isNaN(increasedCost) ? product.cost : increasedCost };
    });

    setProducts(newProducts);
  };

  const decreasePrices = () => {
    if (!products || !products.length || !setProducts) return;

    const newProducts = products.map(product => {
      // Asegurarse de que el costo decrementado sea un número válido.
      const decreasedCost = product.cost * (1 - percentage / 100);
      return { ...product, cost: isNaN(decreasedCost) ? product.cost : decreasedCost };
    });

    setProducts(newProducts);
  };

  const handlePercentageChange = (e) => {
    const input = e.target.value;
    // Convertir el valor del input a número solo si es un valor válido,
    // de lo contrario, usar el valor actual de `percentage`.
    const newPercentage = input ? parseFloat(input) : percentage;

    // Esto evita establecer `NaN` como un valor de estado si `parseFloat` falla
    if (!isNaN(newPercentage)) {
      setPercentage(newPercentage);
    } else {
      setPercentage(0); // O manejar como prefieras si el input es inválido
    }
  };

  return (
    <div>
      <h2>Botón de Ajuste de Precios</h2>
      <div>
        <label>
          Porcentaje:
          <input type="number" value={percentage} onChange={handlePercentageChange} />
        </label>
      </div>
      <div>
        <button onClick={increasePrices}>Aumentar precios</button>
        <button onClick={decreasePrices}>Disminuir precios</button>
      </div>
    </div>
  );
};

export default ProductButton;
