import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';

const AddProductModal = ({ isOpen, onClose, onSubmit, editingProduct }) => {
  const [productDetails, setProductDetails] = useState({
    numeroDeParte: '',
    nombre: '',
    costo: '',
    imagen: null
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [imagePreviewUrl, setImagePreviewUrl] = useState('');

  useEffect(() => {
    if (editingProduct) {
      setProductDetails(editingProduct);
      setImagePreviewUrl(editingProduct.imagen);
    } else {
      // Si no hay producto para editar, limpiamos el estado
      setProductDetails({
        numeroDeParte: '',
        nombre: '',
        costo: '',
        imagen: null
      });
      setImagePreviewUrl('');
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === 'imagen' && files.length > 0) {
      const imageFile = files[0];
      if (!isValidImageFile(imageFile)) {
        setErrorMessage('Seleccione un archivo de imagen válido.');
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;
        setImagePreviewUrl(imageUrl);
        setProductDetails(prevState => ({
          ...prevState,
          imagen: imageUrl
        }));
      };
      reader.readAsDataURL(imageFile);
    } else {
      setProductDetails(prevState => ({
        ...prevState,
        [name]: value
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!productDetails.numeroDeParte || !productDetails.nombre || !productDetails.costo || !productDetails.imagen) {
      setErrorMessage('Todos los campos son requeridos.');
      return;
    }

    onSubmit(productDetails);
    onClose();

    Swal.fire({
      icon: 'success',
      title: '¡Producto agregado!',
      showConfirmButton: false,
      timer: 1500
    });
  };

  const isValidImageFile = (file) => {
    return file.type.startsWith('image/');
  };

  return (
    <>
      {isOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={onClose}>&times;</span>
            <h2>{editingProduct ? "Editar Producto" : "Agregar Producto"}</h2>
            <form onSubmit={handleSubmit}>
              <label>
                Número de Parte:
                <input type="text" name="numeroDeParte" value={productDetails.numeroDeParte} onChange={handleChange} />
              </label>
              <label>
                Nombre:
                <input type="text" name="nombre" value={productDetails.nombre} onChange={handleChange} />
              </label>
              <label>
                Costo:
                <input type="number" name="costo" value={productDetails.costo} onChange={handleChange} />
              </label>
              <label>
                Imagen:
                <input type="file" name="imagen" onChange={handleChange} accept="image/*" />
              </label>
              {imagePreviewUrl && <img src={imagePreviewUrl} alt="Vista previa de la imagen" />}
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              <button type="submit">{editingProduct ? "Guardar Cambios" : "Agregar"}</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default AddProductModal;
