import React, { useState } from 'react';

const ProductButton = ({ products, setProducts }) => {
  const [percentage, setPercentage] = useState(5); // Porcentaje de aumento por defecto

  const increasePrices = () => {
    if (!products || !setProducts) return; // Verificar si products y setProducts están definidos
    const newProducts = products.map(product => {
      const increasedCost = product.cost * (1 + percentage / 100);
      return { ...product, cost: increasedCost };
    });
    setProducts(newProducts);
  };

  const decreasePrices = () => {
    if (!products || !setProducts) return; // Verificar si products y setProducts están definidos
    const newProducts = products.map(product => {
      const decreasedCost = product.cost * (1 - percentage / 100);
      return { ...product, cost: decreasedCost };
    });
    setProducts(newProducts);
  };

  const handlePercentageChange = (e) => {
    const newPercentage = parseFloat(e.target.value);
    setPercentage(newPercentage);
  };

  return (
    <div>
      <h2>Botón de Ajuste de Precios</h2>
      <div>
        <label>
          Porcentaje:
          <input type="number" value={percentage} onChange={handlePercentageChange} />
        </label>
      </div>
      <div>
        <button onClick={increasePrices}>Aumentar precios</button>
        <button onClick={decreasePrices}>Disminuir precios</button>
      </div>
    </div>
  );
};

export default ProductButton;
