import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore';
import { db } from '../firebaseConfig';
import Swal from 'sweetalert2';
import Modal from 'react-modal';
import ClientsTable from '../components/ClientsTable';

Modal.setAppElement('#root');

const ListaClientes = () => {
    const [clientes, setClientes] = useState([]);
    const [formData, setFormData] = useState({
        empresa: '',
        rfc: '',
        regimenFiscal: '',
        moneda: '',
        telefono: '',
        correo: '',
        imagenURL: null,
        domicilio: '',
        numeroExt: '',
        numeroInt: '',
        colonia: '',
        codigoPostal: '',
        ciudad: '',
        estado: ''
    });
    const [modalIsOpen, setModalIsOpen] = useState(false); // Estado para controlar la apertura/cierre del modal
    const [step, setStep] = useState(1); // Estado para controlar el paso del formulario

    useEffect(() => {
        obtenerClientes();
    }, []);

    const obtenerClientes = async () => {
        const clientesSnapshot = await getDocs(collection(db, 'clientes'));
        const listaClientes = clientesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setClientes(listaClientes);
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (step === 1) {
            if (formData.empresa.trim() === '') {
                Swal.fire({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'El campo "Nombre de la Empresa" es obligatorio!'
                });
                return;
            }
            setStep(2);
        } else {
            try {
                if (formData.id) {
                    // Actualizar cliente existente
                    await updateDoc(doc(db, 'clientes', formData.id), formData);
                    Swal.fire({
                        icon: 'success',
                        title: '¡Cliente actualizado con éxito!',
                        showConfirmButton: false,
                        timer: 1000
                    });
                } else {
                    // Agregar nuevo cliente
                    await addDoc(collection(db, 'clientes'), formData);
                    Swal.fire({
                        icon: 'success',
                        title: '¡Cliente registrado con éxito!',
                        showConfirmButton: false,
                        timer: 1000
                    });
                }
                setFormData({
                    empresa: '',
                    rfc: '',
                    regimenFiscal: '',
                    moneda: '',
                    telefono: '',
                    correo: '',
                    imagenURL: null,
                    domicilio: '',
                    numeroExt: '',
                    numeroInt: '',
                    colonia: '',
                    codigoPostal: '',
                    ciudad: '',
                    estado: ''
                });
                setStep(1);
                obtenerClientes(); // Actualizar la lista de clientes después de agregar uno nuevo
                setModalIsOpen(false); // Cerrar el modal después de enviar el formulario
            } catch (error) {
                console.error("Error al agregar cliente:", error);
            }
        }
    };

    const handleEditProduct = (cliente) => {
        // Establecer el estado del formulario con los datos del cliente a editar
        setFormData(cliente);
        // Cambiar el paso al segundo para mostrar el formulario completo
        setStep(2);
        // Abrir el modal
        setModalIsOpen(true);
    };

    const handleDeleteProduct = async (id) => {
        // Mostrar la alerta de confirmación antes de eliminar el cliente
        Swal.fire({
            title: '¿Estás seguro?',
            text: '¡No podrás revertir esto!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminar'
        }).then(async (result) => {
            if (result.isConfirmed) {
                try {
                    await deleteDoc(doc(db, 'clientes', id));
                    Swal.fire({
                        icon: 'success',
                        title: '¡Cliente eliminado con éxito!',
                        showConfirmButton: false,
                        timer: 1000
                    });
                    obtenerClientes(); // Actualizar la lista de clientes después de eliminar uno
                } catch (error) {
                    console.error("Error al eliminar cliente:", error);
                }
            }
        });
    };

    return (
        <div>
            <h1>Lista de Clientes</h1>
            <button onClick={() => setModalIsOpen(true)}>+ Nuevo</button>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={() => setModalIsOpen(false)}
               
            >
                <div style={{ display: "flex", justifyContent: "flex-end", marginBottom: "20px" }}>
                    <button onClick={() => setModalIsOpen(false)} style={{ border: "none", background: "none", cursor: "pointer", fontSize: "20px" }}>×</button>
                </div>
                <h2 style={{textAlign: "center"}}>{formData.id ? 'Editar Cliente' : 'Agregar Nuevo Cliente'}</h2>
                <form onSubmit={handleSubmit} className="client-form">
                    <input type="text" name="empresa" placeholder="Nombre de la Empresa" value={formData.empresa} onChange={handleChange} />
                    <input type="text" name="rfc" placeholder="RFC" value={formData.rfc} onChange={handleChange} />
                    <input type="text" name="regimenFiscal" placeholder="Régimen Fiscal" value={formData.regimenFiscal} onChange={handleChange} />
                    <input type="text" name="moneda" placeholder="Moneda" value={formData.moneda} onChange={handleChange} />
                    <input type="text" name="telefono" placeholder="Teléfono" value={formData.telefono} onChange={handleChange} />
                    <input type="email" name="correo" placeholder="Correo Electrónico" value={formData.correo} onChange={handleChange} />
                    <input type="file" name="imagen" onChange={handleChange} />
                    <button type="submit">{formData.id ? 'Guardar Cambios' : 'Siguiente'}</button>
                </form>
            </Modal>
            <ClientsTable clientes={clientes} onEditProduct={handleEditProduct} onDeleteProduct={handleDeleteProduct} />
        </div>
    );
};

export default ListaClientes;
