import React, { useState } from 'react';
import { Document, Page, Text, View, StyleSheet, PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    padding: 20,
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  text: {
    fontSize: 12,
    marginBottom: 5,
  },
  table: {
    display: 'table',
    width: 'auto',
    borderStyle: 'solid',
    borderWidth: 1,
    borderRightWidth: 0,
    borderBottomWidth: 0,
  },
  tableRow: {
    margin: 'auto',
    flexDirection: 'row',
  },
  tableCell: {
    margin: 'auto',
    width: '25%',
    borderStyle: 'solid',
    borderWidth: 1,
    borderLeftWidth: 0,
    borderTopWidth: 0,
  },
});

function PreviaCotizacion({ cotizacion, numeroCotizacion, clientes, cerrarPrevia }) {
  const { asunto, fechaCotizacion, productosSeleccionados } = cotizacion;

  const [descargandoPDF, setDescargandoPDF] = useState(false);

  // Calcular subtotal, descuento, IVA y total
  const subtotal = productosSeleccionados.reduce((acc, producto) => acc + parseFloat(producto.subtotal), 0);
  const descuentoTotal = productosSeleccionados.reduce((acc, producto) => acc + parseFloat(producto.descuento), 0);
  const iva = subtotal * 0.16; // Suponiendo que el IVA es del 16%
  const total = subtotal - descuentoTotal + iva;

  // FunciÃ³n para generar el documento PDF
  const generatePDF = () => (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.section}>
          <Text style={styles.title}>CotizaciÃ³n</Text>
          <Text>Previa</Text>
          <Text>CotizaciÃ³n: {numeroCotizacion?.toString().padStart(4, '0')}</Text>
          <Text>Fecha de cotizaciÃ³n: {fechaCotizacion}</Text>
          <Text>Asunto: {asunto}</Text>
          <Text>Cliente: {nombreCliente}</Text>
          <Text style={styles.subtitle}>DESCRIPCIÃN</Text>
          <View style={styles.table}>
            <View style={styles.tableRow}>
              <Text style={styles.tableCell}>Cantidad</Text>
              <Text style={styles.tableCell}>ID</Text>
              <Text style={styles.tableCell}>DescripciÃ³n</Text>
              <Text style={styles.tableCell}>Precio</Text>
              <Text style={styles.tableCell}>Subtotal</Text>
            </View>
            {productosSeleccionados.map((producto) => (
              <View style={styles.tableRow} key={producto.id}>
                <Text style={styles.tableCell}>{producto.cantidad}</Text>
                <Text style={styles.tableCell}>{producto.productoIdEditado}</Text>
                <Text style={styles.tableCell}>{producto.nombre}</Text>
                <Text style={styles.tableCell}>${parseFloat(producto.costo).toFixed(2)}</Text>
                <Text style={styles.tableCell}>${parseFloat(producto.subtotal).toFixed(2)}</Text>
              </View>
            ))}
          </View>
          <Text>Subtotal: ${subtotal.toFixed(2)}</Text>
          <Text>Descuento: ${descuentoTotal.toFixed(2)}</Text>
          <Text>IVA: ${iva.toFixed(2)}</Text>
          <Text>Total: ${total.toFixed(2)}</Text>
        </View>
      </Page>
    </Document>
  );

  return (
    <div className="previa-cotizacion">
      <div className="cotizacion-header">
        <img src="/img/logo-iciamex.png" alt="ICIAMEX" className="logoCotizacion" />
        <div className="border-right"></div>
        <h1 className="cotizacion-title">CotizaciÃ³n</h1>
      </div>
      <h1>Previa</h1>
      <h2>CotizaciÃ³n: {numeroCotizacion?.toString().padStart(4, '0')}</h2>
      <hr />
      <p>Fecha de cotizaciÃ³n: {fechaCotizacion}</p>
      <p>Asunto: {asunto}</p>
      <p>Cliente: {nombreCliente}</p>
      <h3>DESCRIPCIÃN</h3>
      <table className="productos-table">
        <thead>
          <tr>
            <th>Cantidad</th>
            <th>ID</th>
            <th>DescripciÃ³n</th>
            <th>Precio</th>
            <th>Subtotal</th>
          </tr>
        </thead>
        <tbody>
          {productosSeleccionados.map((producto) => (
            <tr key={producto.id}>
              <td>{producto.cantidad}</td>
              <td>{producto.productoIdEditado}</td>
              <td>{producto.nombre}</td>
              <td>${parseFloat(producto.costo).toFixed(2)}</td>
              <td>${parseFloat(producto.subtotal).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Subtotal: ${subtotal.toFixed(2)}</h3>
      <h3>Descuento: ${descuentoTotal.toFixed(2)}</h3>
      <h3>IVA: ${iva.toFixed(2)}</h3>
      <h3>Total: ${total.toFixed(2)}</h3>
      <div className="modal-buttons">
        <button onClick={cerrarPrevia} className='eliminarBtnModal'>Cerrar</button>
        <button>Enviar por Correo</button>
        {descargandoPDF ? (
          <button disabled>Descargando...</button>
        ) : (
          <PDFDownloadLink document={generatePDF()} fileName="previa_cotizacion.pdf">
            {({ blob, url, loading, error }) => (
              <button onClick={() => setDescargandoPDF(true)}>Descargar</button>
            )}
          </PDFDownloadLink>
        )}
      </div>
    </div>
  );
}

export default PreviaCotizacion;
