import React, { useState, useEffect } from 'react';
import './App.css';
import ProductTable from './components/ProductTable';
import SearchBar from './components/SearchBar';
import ActionButton from './components/ActionButton';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const storedProducts = JSON.parse(localStorage.getItem('productos'));
    if (storedProducts) {
      setProducts(storedProducts);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('productos', JSON.stringify(products));
  }, [products]);

  const handleSearch = (searchTerm) => {
    // Lógica de búsqueda
    // Puedes implementarlo más adelante si lo necesitas
  };

  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1, // Generar un nuevo ID (solo para este ejemplo)
      numeroDeParte: 'NEW_PART_NUMBER', // Asignar un valor predeterminado para el número de parte
      nombre: 'Nuevo Producto', // Asignar un valor predeterminado para el nombre
      costo: 0, // Asignar un valor predeterminado para el costo
      imagen: 'url_imagen_nueva' // Asignar un valor predeterminado para la imagen
    };
    setProducts(prevProducts => [...prevProducts, newProduct]);
  };

  return (
    <div className="App">
      <h1>Inventario de Productos</h1>
      <SearchBar handleSearch={handleSearch} />
      <ProductTable products={products} />
      <ActionButton text="Agregar Producto" onClick={handleAddProduct} />
    </div>
  );
}

export default App;
