import React, { useState } from 'react';

const AddProductModal = ({ isOpen, onClose, onSubmit }) => {
  const [productDetails, setProductDetails] = useState({
    numeroDeParte: '',
    nombre: '',
    costo: '',
    imagen: ''
  });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Validar que la URL de imagen sea válida
    if (name === 'imagen') {
      if (!isValidImageUrl(value)) {
        setErrorMessage('Ingrese una URL de imagen válida.');
      } else {
        setErrorMessage('');
      }
    }

    setProductDetails(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (errorMessage || !productDetails.imagen) {
      setErrorMessage('Ingrese una URL de imagen válida.');
      return;
    }

    onSubmit(productDetails);
    setProductDetails({
      numeroDeParte: '',
      nombre: '',
      costo: '',
      imagen: ''
    });
    onClose();
  };

  const isValidImageUrl = (url) => {
    // Expresión regular para validar una URL de imagen
    const regex = /\.(jpeg|jpg|gif|png)$/i;
    return regex.test(url);
  };

  return (
    <>
      {isOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={onClose}>&times;</span>
            <h2>Agregar Producto</h2>
            <form onSubmit={handleSubmit}>
              <label>
                Número de Parte:
                <input type="text" name="numeroDeParte" value={productDetails.numeroDeParte} onChange={handleChange} />
              </label>
              <label>
                Nombre:
                <input type="text" name="nombre" value={productDetails.nombre} onChange={handleChange} />
              </label>
              <label>
                Costo:
                <input type="number" name="costo" value={productDetails.costo} onChange={handleChange} />
              </label>
              <label>
                Imagen:
                <input type="url" name="imagen" value={productDetails.imagen} onChange={handleChange} accept="image/*" />
              </label>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              <button type="submit">Agregar</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default AddProductModal;
