import React, { useState } from 'react';
import ActionButton from './ActionButton';

const ProductRow = ({ cliente, isEditing, handleEdit, handleCancelEdit, onDeleteProduct, onUpdateProduct }) => {
  const [editedClient, setEditedClient] = useState({ ...cliente });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedClient(prevClient => ({
      ...prevClient,
      [name]: value
    }));
  };

  const saveChanges = () => {
    onUpdateProduct(editedClient);
  };

  return (
    <tr>
      <td>{cliente.id}</td>
      <td>{isEditing ? <input type="text" name="empresa" value={editedClient.empresa} onChange={handleChange} /> : cliente.empresa}</td>
      <td>{isEditing ? <input type="text" name="rfc" value={editedClient.rfc} onChange={handleChange} /> : cliente.rfc}</td>
      <td>{isEditing ? <input type="text" name="regimenFiscal" value={editedClient.regimenFiscal} onChange={handleChange} /> : cliente.regimenFiscal}</td>
      <td>{isEditing ? <input type="text" name="moneda" value={editedClient.moneda} onChange={handleChange} /> : cliente.moneda}</td>
      <td>{isEditing ? <input type="text" name="telefono" value={editedClient.telefono} onChange={handleChange} /> : cliente.telefono}</td>
      <td>{isEditing ? <input type="text" name="correo" value={editedClient.correo} onChange={handleChange} /> : cliente.correo}</td>
      <td>
        {isEditing ? (
          <>
            <ActionButton text="Guardar" onClick={saveChanges} />
            <ActionButton text="Cancelar" onClick={handleCancelEdit} />
          </>
        ) : (
          <>
            <ActionButton text="Editar" onClick={() => handleEdit(cliente.id)} />
            <ActionButton text="Eliminar" onClick={() => onDeleteProduct(cliente.id)} />
          </>
        )}
      </td>
    </tr>
  );
};

export default ProductRow;
