import React, { useState } from 'react';
import { collection, addDoc } from 'firebase/firestore';
import { db } from '../firebaseConfig';

const ListaClientes = () => {
    const [formData, setFormData] = useState({
        id: '',
        empresa: '',
        rfc: '',
        regimenFiscal: '',
        moneda: '',
        telefono: '',
        correo: '',
        imagenURL: null // Cambiar el valor inicial a null
    });

    const handleChange = (e) => {
        if (e.target.name === 'imagen') {
            // Si el campo es el de la imagen, guardar el archivo seleccionado
            setFormData({
                ...formData,
                imagenURL: e.target.files[0] // Guardar el archivo seleccionado
            });
        } else {
            setFormData({
                ...formData,
                [e.target.name]: e.target.value
            });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let imageURL = null;
            if (formData.imagenURL) {
                // Si hay una imagen seleccionada, convertirla en una URL de datos
                imageURL = await convertImageToDataURL(formData.imagenURL);
            }
    
            const clientData = {
                ...formData,
                imagenURL: imageURL
            };
    
            const docRef = await addDoc(collection(db, 'clientes'), clientData);
            console.log("Cliente registrado con ID: ", docRef.id);
            setFormData({
                id: '',
                empresa: '',
                rfc: '',
                regimenFiscal: '',
                moneda: '',
                telefono: '',
                correo: '',
                imagenURL: null // Reiniciar la imagen a null después de enviar el formulario
            });
        } catch (error) {
            console.error("Error al agregar cliente: ", error);
        }
    };
    
    // Función para convertir una imagen en un URL de datos (Data URL)
    const convertImageToDataURL = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result);
            reader.onerror = reject;
            reader.readAsDataURL(file);
        });
    };
    

    return (
        <form onSubmit={handleSubmit} className="client-form">
            <input type="text" name="id" placeholder="ID" value={formData.id} onChange={handleChange} />
            <input type="text" name="empresa" placeholder="Nombre de la empresa" value={formData.empresa} onChange={handleChange} />
            <input type="text" name="rfc" placeholder="RFC" value={formData.rfc} onChange={handleChange} />
            <input type="text" name="regimenFiscal" placeholder="Régimen Fiscal" value={formData.regimenFiscal} onChange={handleChange} />
            <input type="text" name="moneda" placeholder="Moneda" value={formData.moneda} onChange={handleChange} />
            <input type="text" name="telefono" placeholder="Teléfono" value={formData.telefono} onChange={handleChange} />
            <input type="email" name="correo" placeholder="Correo electrónico" value={formData.correo} onChange={handleChange} />
            <input type="file" name="imagen" onChange={handleChange} />
            <button type="submit">Agregar Cliente</button>
        </form>
    );
};

export default ListaClientes;
