import React, { useState, useEffect } from 'react';
import './App.css';
import ProductTable from './components/ProductTable';
import SearchBar from './components/SearchBar';
import ActionButton from './components/ActionButton';
import AddProductModal from './components/AddProductModal';
import { db } from './firebaseConfig'; // Importa la instancia de Firebase

function App() {
  const [products, setProducts] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productsSnapshot = await getDocs(collection(db, 'productos'));
        const productsData = productsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setProducts(productsData);
      } catch (error) {
        console.error('Error al obtener los productos:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddProduct = async (newProduct) => {
    try {
      const docRef = await addDoc(collection(db, 'productos'), newProduct);
      setProducts(prevProducts => [...prevProducts, { id: docRef.id, ...newProduct }]);
      setIsModalOpen(false);
      setEditingProduct(null);
    } catch (error) {
      console.error('Error al agregar el producto:', error);
    }
  };

  const handleEditProduct = async (updatedProduct) => {
    try {
      await updateDoc(doc(db, 'productos', updatedProduct.id), updatedProduct);
      const updatedProducts = products.map(product =>
        product.id === updatedProduct.id ? updatedProduct : product
      );
      setProducts(updatedProducts);
      setIsModalOpen(false);
      setEditingProduct(null);
    } catch (error) {
      console.error('Error al editar el producto:', error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await deleteDoc(doc(db, 'productos', productId));
      const updatedProducts = products.filter(product => product.id !== productId);
      setProducts(updatedProducts);
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
    }
  };

  useEffect(() => {
    filterProducts('');
  }, [products]);

  const handleSearch = (searchTerm) => {
    filterProducts(searchTerm);
  };

  const filterProducts = (searchTerm) => {
    const filtered = products.filter(product =>
      (product.numeroDeParte?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.nombre?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.descripcion?.toLowerCase().includes(searchTerm.toLowerCase()))
    );
    setFilteredProducts(filtered);
  };

  return (
    <div className="App">
      <h1 style={{ fontFamily: "Montserrat, sans-serif" }}>Inventario de Productos ICIAMEX</h1>
      <SearchBar handleSearch={handleSearch} />
      <ProductTable
        products={filteredProducts}
        onEditProduct={(productId) => {
          const productToEdit = products.find(product => product.id === productId);
          setEditingProduct(productToEdit);
          setIsModalOpen(true);
        }}
        onDeleteProduct={handleDeleteProduct}
      />
      <ActionButton text="Agregar Producto" onClick={() => {
        setIsModalOpen(true);
        setEditingProduct(null);
      }} />
      <AddProductModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={editingProduct ? handleEditProduct : handleAddProduct}
        editingProduct={editingProduct}
      />
    </div>
  );
}

export default App;
