import React, { useState } from 'react';
import Modal from 'react-modal';

// Establece la funci贸n de inicializaci贸n de react-modal para evitar un aviso de desenfoque de accesibilidad
Modal.setAppElement('#root');

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
    border: 'none',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    padding: '20px',
    maxWidth: '400px',
    width: '100%',
    maxHeight: '90vh',
    overflow: 'auto',
  },
  label: {
    display: 'block',
    marginBottom: '10px',
  },
  input: {
    width: '100%',
    padding: '8px',
    marginBottom: '20px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    boxSizing: 'border-box',
  },
};

const ProductButton = ({ products, setProducts }) => {
  const [percentage, setPercentage] = useState(10);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const updatePrices = (increase) => {
    const adjustFactor = 1 + (increase ? percentage : -percentage) / 100;
    const adjustedProducts = products.map(product => ({
      ...product,
      costo: product.costo * adjustFactor,
    }));
    setProducts(adjustedProducts);
    setModalIsOpen(false);
  };

  const handlePercentageChange = (e) => {
    setPercentage(parseFloat(e.target.value) || 0);
  };

  return (
    <div style={{ position: 'fixed', top: '20px', right: '200px', zIndex: '9999' }}>
      <button className="modal-button" onClick={() => setModalIsOpen(true)}>Ajustar precios</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        style={{...customStyles, content: { ...customStyles.content, width: '70%' }}}
        contentLabel="Ajuste de Precios"
      >
        <button className="modal-close" onClick={() => setModalIsOpen(false)}>x</button>
        <h2>Editar Ajuste de Precios</h2>
        <label style={customStyles.label}>
          Porcentaje:
          <input type="number" value={percentage} onChange={handlePercentageChange} style={customStyles.input} />
        </label>
        <button className="modal-apply-button" onClick={() => updatePrices(true)}>Aplicar Aumento</button>
        <button onClick={() => updatePrices(false)}>Aplicar Descuento</button>
      </Modal>
    </div>
  );
};

export default ProductButton;
