import React, { useState } from 'react';

const AddProductModal = ({ isOpen, onClose, onSubmit }) => {
  const [productDetails, setProductDetails] = useState({
    numeroDeParte: '',
    nombre: '',
    costo: '',
    imagen: null // Cambiamos a null para almacenar el archivo de imagen
  });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    // Validar que el campo sea para imagen y hay un archivo seleccionado
    if (name === 'imagen' && files.length > 0) {
      const imageFile = files[0];
      if (!isValidImageFile(imageFile)) {
        setErrorMessage('Seleccione un archivo de imagen válido.');
        return;
      }
      // Actualizar el estado con el archivo de imagen seleccionado
      setProductDetails(prevState => ({
        ...prevState,
        [name]: imageFile
      }));
    } else {
      setProductDetails(prevState => ({
        ...prevState,
        [name]: value
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!productDetails.imagen) {
      setErrorMessage('Seleccione una imagen.');
      return;
    }

    onSubmit(productDetails);
    setProductDetails({
      numeroDeParte: '',
      nombre: '',
      costo: '',
      imagen: null
    });
    onClose();
  };

  const isValidImageFile = (file) => {
    return file.type.startsWith('image/');
  };

  return (
    <>
      {isOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={onClose}>&times;</span>
            <h2>Agregar Producto</h2>
            <form onSubmit={handleSubmit}>
              <label>
                Número de Parte:
                <input type="text" name="numeroDeParte" value={productDetails.numeroDeParte} onChange={handleChange} />
              </label>
              <label>
                Nombre:
                <input type="text" name="nombre" value={productDetails.nombre} onChange={handleChange} />
              </label>
              <label>
                Costo:
                <input type="number" name="costo" value={productDetails.costo} onChange={handleChange} />
              </label>
              <label>
                Imagen:
                <input type="file" name="imagen" onChange={handleChange} accept="image/*" />
              </label>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              <button type="submit">Agregar</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default AddProductModal;
