import React from 'react';
import { PDFDownloadLink, Document, Page, Text, View, StyleSheet, Image } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    padding: 20,
  },
  section: {
    marginVertical: 10,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
    textAlign: 'center',
  },
  subtitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
    textAlign: 'center',
    marginTop: 10,
  },
  text: {
    fontSize: 14,
    fontWeight: 'bold',
    marginTop: 10,
  },
  table: {
    width: '100%',
    borderStyle: 'solid',
    borderWidth: 1,
    borderColor: '#000',
  },
  tableRow: {
    flexDirection: 'row',
  },
  tableCell: {
    flex: 1,
    borderStyle: 'solid',
    borderWidth: 1,
    fontSize: 12,
    borderColor: '#000',
    padding: 5,
  },
  logoContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
    marginBottom: 10,
  },
  logo: {
    width: 100,
    marginRight: 10,
    borderRightWidth: 1,
    borderRightColor: '#ccc',
  },
  details: {
    fontSize: 14,
    marginBottom: 5,
  },
});

function PreviaCotizacion({ cotizacion, numeroCotizacion, clientes, cerrarPrevia }) {

  const { asunto, fechaCotizacion, productosSeleccionados } = cotizacion;

  // Buscamos el cliente correspondiente en la lista de clientes
  const clienteEncontrado = clientes.find(cliente => cliente.id === cotizacion.cliente);

  // Verificamos si se encontró el cliente
  const nombreCliente = clienteEncontrado ? clienteEncontrado.empresa : 'Cliente no encontrado';

   // Función para formatear números con comas para separar miles
  const formatNumber = (number) => {
    return number.toLocaleString('en-US', { maximumFractionDigits: 2 });
  };

  // Calcular subtotal
  const subtotal = productosSeleccionados.reduce((acc, producto) => acc + parseFloat(producto.subtotal), 0);

  // Calcular descuento total (suponiendo que hay un descuento en cada producto)
  const descuentoTotal = productosSeleccionados.reduce((acc, producto) => acc + parseFloat(producto.descuento), 0);

  // Calcular IVA
  const iva = subtotal * 0.16; // Suponiendo que el IVA es del 16%

  // Calcular total
  const total = subtotal - descuentoTotal + iva;

  const generatePDF = () => (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.section}>
          <View style={styles.logoContainer}>
            <Image src="/img/logo-iciamex.png" style={styles.logo} />
            <Text style={styles.title}>Cotización</Text>
          </View>
          <Text style={styles.title}>Previa de Cotización</Text>
          <Text style={styles.details}>Cotización: {numeroCotizacion?.toString().padStart(4, '0')}</Text>
          <Text style={styles.details}>Fecha de cotización: {fechaCotizacion}</Text>
          <Text style={styles.details}>Asunto: {asunto}</Text>
          <Text style={styles.details}>Cliente: {nombreCliente}</Text>
          <Text style={styles.subtitle}>DESCRIPCIÓN</Text>
          <View style={styles.table}>
            <View style={styles.tableRow}>
              <Text style={styles.tableCell}>Cantidad</Text>
              <Text style={styles.tableCell}>ID</Text>
              <Text style={styles.tableCell}>Descripción</Text>
              <Text style={styles.tableCell}>Precio</Text>
              <Text style={styles.tableCell}>Subtotal</Text>
            </View>
            {productosSeleccionados.map((producto) => (
              <View style={styles.tableRow} key={producto.id}>
                <Text style={styles.tableCell}>{producto.cantidad}</Text>
                <Text style={styles.tableCell}>{producto.productoIdEditado}</Text>
                <Text style={styles.tableCell}>{producto.nombre}</Text>
                <Text style={styles.tableCell}>${formatNumber((producto.costo.toFixed(2)))}</Text>
                <Text style={styles.tableCell}>${formatNumber((producto.subtotal.toFixed(2)))}</Text>
              </View>
            ))}
          </View>
          <Text style={styles.text}>Subtotal: ${formatNumber(subtotal)}</Text>
          <Text style={styles.text}>Descuento: ${formatNumber(descuentoTotal)}</Text>
          <Text style={styles.text}>IVA: ${formatNumber(iva)}</Text>
          <Text style={styles.text}>Total: ${formatNumber(total)}</Text>
        </View>
      </Page>
    </Document>
  );

  return (
    <div className="previa-cotizacion">
      <div className="cotizacion-header">
          <img src="/img/logo-iciamex.png" alt="ICIAMEX" className="logoCotizacion" />
          <div className="border-right"></div>
          <h1 className="cotizacion-title">Cotización</h1>
      </div>
      <h1>Previa</h1>
      <h2>Cotización: {numeroCotizacion?.toString().padStart(4, '0')}</h2>
      <hr />
      <p>Fecha de cotización: {fechaCotizacion}</p>
      <p>Asunto: {asunto}</p>
      <p>Cliente: {nombreCliente}</p>
      <h3>DESCRIPCIÓN</h3>
      <table className="productos-table">
        <thead>
          <tr>
            <th>Cantidad</th>
            <th>ID</th>
            <th>Descripción</th>
            <th>Precio</th>
            <th>Subtotal</th>
          </tr>
        </thead>
        <tbody>
          {productosSeleccionados.map((producto) => (
            <tr key={producto.id}>
              <td>{producto.cantidad}</td>
              <td>{producto.productoIdEditado}</td>
              <td>{producto.nombre}</td>
              <td>${parseFloat(producto.costo).toFixed(2)}</td>
              <td>${parseFloat(producto.subtotal).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Subtotal: ${formatNumber(subtotal)}</h3>
      <h3>Descuento: ${formatNumber(descuentoTotal)}</h3>
      <h3>IVA: ${formatNumber(iva)}</h3>
      <h3>Total: ${formatNumber(total)}</h3>
      <div className="modal-buttons">
        <button onClick={cerrarPrevia} className='eliminarBtnModal'>Cerrar</button>
        <button>Enviar por Correo</button>
        <PDFDownloadLink document={generatePDF()} fileName="previa_cotizacion.pdf">
          {({ loading }) => (loading ? <button disabled>Descargando...</button> : <button>Descargar</button>)}
        </PDFDownloadLink>
      </div>
    </div>
  );
}

export default PreviaCotizacion;
