import React, { useState } from 'react';
import { collection, addDoc } from 'firebase/firestore';
import { db } from '../firebaseConfig';
import Swal from 'sweetalert2';

const AddClientForm = () => {
    const [formData, setFormData] = useState({
        id: '',
        empresa: '',
        rfc: '',
        regimenFiscal: '',
        moneda: '',
        telefono: '',
        correo: '',
        imagenURL: null, // Cambiar el valor inicial a null
        domicilio: '',
        numeroExt: '',
        numeroInt: '',
        colonia: '',
        codigoPostal: '',
        ciudad: '',
        estado: ''
    });

    const [step, setStep] = useState(1); // Estado para controlar el paso del formulario

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (step === 1) {
            // Si es el primer paso, avanzar al segundo paso
            if (!validateStepOne()) {
                // Si los campos requeridos del primer paso no están completos, mostrar alerta
                Swal.fire({
                    icon: 'error',
                    title: '¡Error!',
                    text: 'Todos los campos son obligatorios',
                });
                return;
            }
            setStep(2);
        } else {
            // Si es el segundo paso, enviar los datos a Firebase
            if (!validateStepTwo()) {
                // Si los campos requeridos del segundo paso no están completos, mostrar alerta
                Swal.fire({
                    icon: 'error',
                    title: '¡Error!',
                    text: 'Todos los campos son obligatorios',
                });
                return;
            }
            try {
                const clientData = {
                    ...formData
                };
                await addDoc(collection(db, 'clientes'), clientData);
                Swal.fire({
                    icon: 'success',
                    title: '¡Cliente registrado con éxito!',
                    showConfirmButton: false,
                    timer: 1500
                }).then(() => {
                    // Reiniciar el formulario después de enviar los datos
                    setFormData({
                        id: '',
                        empresa: '',
                        rfc: '',
                        regimenFiscal: '',
                        moneda: '',
                        telefono: '',
                        correo: '',
                        imagenURL: null,
                        domicilio: '',
                        numeroExt: '',
                        numeroInt: '',
                        colonia: '',
                        codigoPostal: '',
                        ciudad: '',
                        estado: ''
                    });
                    setStep(1); // Volver al primer paso
                });
            } catch (error) {
                console.error("Error al agregar cliente: ", error);
            }
        }
    };

    // Función para validar los campos requeridos del primer paso
    const validateStepOne = () => {
        const { id, empresa, rfc, regimenFiscal, moneda, telefono, correo, imagenURL } = formData;
        return id && empresa && rfc && regimenFiscal && moneda && telefono && correo && imagenURL;
    };

    // Función para validar los campos requeridos del segundo paso
    const validateStepTwo = () => {
        const { domicilio, numeroExt, colonia, codigoPostal, ciudad, estado } = formData;
        return domicilio && numeroExt && colonia && codigoPostal && ciudad && estado;
    };

    return (
        <div>
            {step === 1 && (
                <form onSubmit={handleSubmit} className="client-form">
                    {/* Campos del primer formulario */}
                </form>
            )}
            {step === 2 && (
                <form onSubmit={handleSubmit} className="client-form">
                    {/* Campos del segundo formulario */}
                </form>
            )}
        </div>
    );
};

export default AddClientForm;
