import React, { useState, useEffect } from 'react';
import './App.css';
import ProductTable from './components/ProductTable';
import SearchBar from './components/SearchBar';
import ActionButton from './components/ActionButton';
import AddProductModal from './components/AddProductModal';

function App() {
  const [products, setProducts] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const storedProducts = JSON.parse(localStorage.getItem('productos'));
    if (storedProducts) {
      setProducts(storedProducts);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('productos', JSON.stringify(products));
  }, [products]);

  const handleSearch = (searchTerm) => {
    // Implementa la lógica de búsqueda aquí
    // Por ejemplo, filtra los productos cuyo nombre coincida con searchTerm
    // Actualiza el estado de los productos con los resultados de búsqueda
  };

  const handleAddProduct = (newProduct) => {
    setProducts(prevProducts => [...prevProducts, { id: products.length + 1, ...newProduct }]);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleEditProduct = (productId, updatedProduct) => {
    // Implementa la lógica para editar un producto
    // productId es el ID del producto a editar
    // updatedProduct es el objeto que contiene los datos actualizados del producto
  };

  const handleDeleteProduct = (productId) => {
    // Implementa la lógica para eliminar un producto
    // productId es el ID del producto a eliminar
  };

  return (
    <div className="App">
      <h1>Inventario de Productos</h1>
      <SearchBar handleSearch={handleSearch} />
      <ProductTable products={products} onEditProduct={handleEditProduct} onDeleteProduct={handleDeleteProduct} />
      <ActionButton text="Agregar Producto" onClick={handleAddProduct} />
      <AddProductModal isOpen={isModalOpen} onClose={closeModal} onSubmit={handleAddProduct} />
    </div>
  );
}

export default App;
