import React, { useState } from 'react';
import Modal from 'react-modal';


const ProductButton = ({ products, setProducts }) => {
  const [percentage, setPercentage] = useState(10);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const updatePrices = (increase) => {
    const adjustFactor = 1 + (increase ? percentage : -percentage) / 100;
    const adjustedProducts = products.map(product => ({
      ...product,
      costo: product.costo * adjustFactor,
    }));
    setProducts(adjustedProducts);
    setModalIsOpen(false);
  };

  const handlePercentageChange = (e) => {
    setPercentage(parseFloat(e.target.value) || 0);
  };

  return (
    <div className="product-button-container">
      <h1 className="product-button-title">Ajuste de Precios</h1>
      <button className="modal-button" onClick={() => setModalIsOpen(true)}>Ajustar precios</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        style={{...customStyles, content: { ...customStyles.content, width: '70%' }}}
        contentLabel="Ajuste de Precios"
      >
        <button className="modal-close" onClick={() => setModalIsOpen(false)}>Cerrar</button>
        <h2>Editar Ajuste de Precios</h2>
        <label style={customStyles.label}>
          Porcentaje:
          <input type="number" value={percentage} onChange={handlePercentageChange} style={customStyles.input} />
        </label>
        <button className="modal-apply-button" onClick={() => updatePrices(true)}>Aplicar Aumento</button>
        <button className="modal-apply-button" onClick={() => updatePrices(false)}>Aplicar Descuento</button>
      </Modal>
    </div>
  );
};

export default ProductButton;
