import React, { useState } from 'react';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      {/* Mover el ícono del menú fuera del sidebar y hacerlo siempre accesible */}
      <img
        className="menu-icon"
        src="/img/menu.svg"
        alt="Menu Icon"
        onClick={toggleSidebar}
        style={{
          position: 'fixed',
          top: '20px',
          right: isOpen ? '250px' : '20px', // Ajusta según el ancho de tu sidebar
          zIndex: 1100, // Asegúrate de que el z-index sea mayor que el del sidebar
          cursor: 'pointer',
        }}
      />

      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <a className="centrar-logo" href="/">
            <img className="logo" src="/img/logo-iciamex.png" alt="ICIAMEX logo" />
          </a>
        </div>
        <div className="sidebar-content">
          <nav id="primary-menu" className="navbar">
            <ul className="nav navbar-nav">
              <li><a className="active" href="index.html">Ordenes de Compra</a></li>
              <li><a href="/">Cotizaciones</a></li>
              <li><a href="/">Facturas</a></li>
              <li><a href="/">Lista de Proveedores</a></li>
              <li><a href="/">Lista de Clientes</a></li>
              <li><a href="/">Lista de Artículos</a></li>
              <li><a href="/">Informes</a></li>
            </ul>
          </nav>
        </div>
        <div className="sidebar-footer">
          <div className="sidebar-copyright">&copy; 2024 ICIAMEX</div>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
