import React, { useState, useEffect } from 'react';
import './App.css';
import ProductTable from './components/ProductTable';
import SearchBar from './components/SearchBar';
import ActionButton from './components/ActionButton';
import AddProductModal from './components/AddProductModal';

function App() {
  const [products, setProducts] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null); // Nuevo estado para el producto que se está editando

  useEffect(() => {
    const storedProducts = JSON.parse(localStorage.getItem('productos'));
    if (storedProducts) {
      setProducts(storedProducts);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('productos', JSON.stringify(products));
  }, [products]);

  const handleSearch = (searchTerm) => {
    // Implementa la lógica de búsqueda aquí
    // Por ejemplo, filtra los productos cuyo nombre coincida con searchTerm
    // Actualiza el estado de los productos con los resultados de búsqueda
  };

  const handleAddProduct = (newProduct) => {
    setProducts(prevProducts => [...prevProducts, { id: products.length + 1, ...newProduct }]);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const openEditModal = (productId) => { // Nueva función para abrir el modal de edición
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit);
    setIsModalOpen(true);
  };

  const handleEditProduct = (updatedProduct) => {
    const updatedProducts = products.map(product => {
      if (product.id === updatedProduct.id) {
        return updatedProduct; // Actualiza el producto existente
      } else {
        return product; // Mantén los otros productos sin cambios
      }
    });
    setProducts(updatedProducts);
    setEditingProduct(null); // Limpia el producto en edición
    setIsModalOpen(false); // Cierra el modal de edición
  };

  const handleDeleteProduct = (productId) => {
    // Filtrar los productos para excluir el producto que se va a eliminar
    const updatedProducts = products.filter(product => product.id !== productId);
    // Actualizar el estado con la nueva lista de productos
    setProducts(updatedProducts);
  };

  return (
    <div className="App">
      <h1 style={{ fontFamily: "Montserrat, sans-serif" }}>Inventario de Productos ICIAMEX</h1>
      <SearchBar handleSearch={handleSearch} />
      <ProductTable products={products} onEditProduct={openEditModal} onDeleteProduct={handleDeleteProduct} />
      <ActionButton text="Agregar Producto" onClick={openModal} />
      <AddProductModal isOpen={isModalOpen} onClose={closeModal} onSubmit={handleAddProduct} editingProduct={editingProduct} onEdit={handleEditProduct} /> {/* Pasar el producto en edición y la función de edición al modal */}
    </div>
  );
}

export default App;
