import React, { useState } from 'react';
import Modal from 'react-modal';
import { doc, updateDoc } from 'firebase/firestore'; // Asegúrate de importar esto
import db from '../firebaseConfig'; // Verifica que la ruta a tu configuración de Firebase sea correcta

// Establece la función de inicialización de react-modal para evitar un aviso de desenfoque de accesibilidad
Modal.setAppElement('#root');

const customStyles = {
  // tus estilos aquí
};

const ProductButton = ({ products, setProducts, reloadProducts }) => { // Asegúrate de recibir reloadProducts como prop
  const [percentage, setPercentage] = useState(10);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const updatePrices = async (increase) => {
    const adjustFactor = parseFloat((1 + (increase ? percentage : -percentage) / 100).toFixed(2));

    const updatePromises = products.map(product => {
      const newCost = parseFloat((product.costo * adjustFactor).toFixed(2));
      const productRef = doc(db, 'productos', product.id);
      return updateDoc(productRef, { costo: newCost });
    });

    try {
      await Promise.all(updatePromises);
      alert('Todos los precios han sido actualizados correctamente.');
      reloadProducts(); // Recarga los productos desde Firebase para actualizar el estado local
    } catch (error) {
      console.error('Error al actualizar los precios:', error);
      alert('Ocurrió un error al actualizar los precios.');
    }

    setModalIsOpen(false);
  };

  const handlePercentageChange = (e) => {
    setPercentage(parseFloat(e.target.value) || 0);
  };

  return (
    <div style={{ position: 'fixed', top: '20px', right: '100px', zIndex: '9999' }}>
      <button onClick={() => setModalIsOpen(true)}>Ajustar precios</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        style={customStyles}
        contentLabel="Ajuste de Precios"
      >
        <button onClick={() => setModalIsOpen(false)}>x</button>
        <h2>Editar Ajuste de Precios</h2>
        <label>
          Porcentaje:
          <input type="number" value={percentage} onChange={handlePercentageChange} />
        </label>
        <button onClick={() => updatePrices(true)}>Aumentar Precio</button>
        <button onClick={() => updatePrices(false)}>Disminuir Precio</button>
      </Modal>
    </div>
  );
};

export default ProductButton;
