import React, { useState } from 'react';
import Modal from './Modal'; // Importa el componente Modal
import PreviaCotizacion from './PreviaCotizacion'; // Importa el componente PreviaCotizacion

function TablaCotizaciones({ cotizaciones }) {
  const [modalIsOpen, setModalIsOpen] = useState(false); // Estado para controlar si el modal está abierto
  const [cotizacionSeleccionada, setCotizacionSeleccionada] = useState(null); // Estado para almacenar la cotización seleccionada

  const verPrevia = (cotizacion) => {
    setCotizacionSeleccionada(cotizacion); // Almacena la cotización seleccionada
    setModalIsOpen(true); // Abre el modal
  };

  const cerrarModalPrevia = () => {
    setCotizacionSeleccionada(null); // Limpia la cotización seleccionada
    setModalIsOpen(false); // Cierra el modal
  };

  return (
    <div className="cotizaciones-table">
      <h2>Lista de Cotizaciones</h2>
      <table>
        <thead>
          <tr>
            <th>Fecha de Cotización</th>
            <th>No. Cotización</th>
            <th>Asunto</th>
            <th>Nombre del Cliente</th>
            <th>Total</th>
            <th>Status</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {cotizaciones.map((cotizacion, index) => (
            <tr key={index}>
              <td>{cotizacion.fechaCotizacion}</td>
              <td>{cotizacion.numeroCotizacion?.toString().padStart(4, '0')}</td>
              <td>{cotizacion.asunto}</td>
              <td>{cotizacion.nombreCliente}</td>
              <td>${cotizacion.total?.toFixed(2)}</td>
              <td>{cotizacion.estado}</td>
              <td>
                <button onClick={() => verPrevia(cotizacion)}>Ver Previa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Renderiza el modal solo si modalIsOpen es true */}
      {modalIsOpen && (
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={cerrarModalPrevia}
          contentLabel="Vista Previa"
          style={customStyles}
        >
          <PreviaCotizacion
            cotizacion={cotizacionSeleccionada} // Pasa la cotización seleccionada al componente PreviaCotizacion
            cerrarPrevia={cerrarModalPrevia} // Pasa la función para cerrar el modal
          />
        </Modal>
      )}
    </div>
  );
}

export default TablaCotizaciones;
