import React, { useState, useEffect } from 'react';
import { collection, getDocs, deleteDoc, doc } from 'firebase/firestore';
import { db } from '../firebaseConfig';

function CotizacionForm(  ) {
  const [cliente, setCliente] = useState('');
  const [clientes, setClientes] = useState([]);
  const [productos, setProductos] = useState([]);
  const [asunto, setAsunto] = useState('');
  const [fechaCotizacion, setFechaCotizacion] = useState(getCurrentDate());
  const [fechaVencimiento, setFechaVencimiento] = useState('');


  useEffect(() => {
    obtenerClientes();
    obtenerProductos();
  }, []);

  const obtenerClientes = async () => {
    try {
      const clientesSnapshot = await getDocs(collection(db, 'clientes'));
      const listaClientes = clientesSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setClientes(listaClientes);
    } catch (error) {
      console.error('Error al obtener clientes:', error);
    }
  };

  const obtenerProductos = async () => {
    try {
      const productosSnapshot = await getDocs(collection(db, 'productos'));
      const listaProductos = productosSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setProductos(listaProductos);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
    }
  };

  // Función para obtener la fecha actual
  function getCurrentDate() {
    const date = new Date();
    return date.toISOString().split('T')[0]; // Formato yyyy-mm-dd
  }

  // Función para agregar otro producto
  function agregarProducto() {
    setProductos([...productos, { nombre: '', cantidad: 1, impuesto: '', costo: 0 }]);
  }

  const eliminarProducto = async (index) => {
    const idProducto = productos[index].id; // Obtiene el ID del producto a eliminar
    try {
      await deleteDoc(doc(db, 'productos', idProducto)); // Elimina el documento de Firestore
      const nuevosProductos = productos.filter((_, i) => i !== index); // Filtra los productos para eliminar el seleccionado
      setProductos(nuevosProductos);
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
    }
  };

  return (
   <div className="cotizacion-form">
  <form>
    <div className="cotizacion-header">
      <img src="/img/logo-iciamex.png" alt="ICIAMEX" className="logoCotizacion" />
      <div className="border-right"></div>
      <h1 className="cotizacion-title">Cotización</h1>
    </div>

    <div className="cotizacion-body">
      <label htmlFor="cliente">Cliente:</label>
      <select id="cliente" name="cliente" value={cliente} onChange={(e) => setCliente(e.target.value)}>
          <option value="">Seleccionar cliente</option>
          {clientes.map((cliente) => (
            <option key={cliente.id} value={cliente.id}>
              {cliente.empresa}
            </option>
          ))}
      </select><br />

      <label htmlFor="asunto">Asunto:</label>
      <input type="text" id="asunto" name="asunto" value={asunto} onChange={(e) => setAsunto(e.target.value)} /><br />

      <label htmlFor="fecha-cotizacion">Fecha de cotización:</label>
      <input type="text" id="fecha-cotizacion" name="fecha-cotizacion" value={fechaCotizacion} readOnly /><br />

      <label htmlFor="fecha-vencimiento">Fecha de vencimiento:</label>
      <input type="date" id="fecha-vencimiento" name="fecha-vencimiento" value={fechaVencimiento} onChange={(e) => setFechaVencimiento(e.target.value)} /><br />

      {/* Aquí puedes agregar campos para productos dinámicamente */}
      <h2>Productos:</h2>
        <table className="productos-table">
          <thead>
            <tr>
              <th>Artículo</th>
              <th>Cantidad</th>
              <th>Impuesto</th>
              <th>Precio</th>
              <th></th> {/* Espacio para el botón de eliminar */}
            </tr>
          </thead>
          <tbody>
            {productos.map((product, index) => (
              <tr key={index}>
                <td>{product.nombre}</td> {/* Mostrar el nombre del producto */}
                <td>{product.cantidad}</td> {/* Mostrar la cantidad */}
                <td>{product.impuesto}</td> {/* Mostrar el impuesto */}
                <td>${product.costo}</td> {/* Mostrar el precio */}
                <td>
                  <button onClick={() => eliminarProducto(index)}>✖︎</button> {/* Botón para eliminar */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
<button onClick={agregarProducto}>+ Añadir Producto</button>

      {/* Separador */}
      <hr />

      {/* Datos de guardado */}
      <p>Guardado por última vez: Hoy a las 4:30 p.m</p>
      <button type="button">Cancelar</button>
      <button type="button">Guardar</button>
    </div>
  </form>
</div>



  );
}

export default CotizacionForm;
