import React, { useState, useEffect } from 'react';

const AddProductModal = ({ isOpen, onClose, onSubmit, editingProduct, onEdit }) => {
    const [productDetails, setProductDetails] = useState({
        numeroDeParte: '',
        nombre: '',
        costo: '',
        imagen: null
    });

    useEffect(() => {
        if (editingProduct) {
            setProductDetails(editingProduct);
        }
    }, [editingProduct]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProductDetails(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (editingProduct) {
            onEdit(productDetails); // Llama a la función de edición
        } else {
            onSubmit(productDetails);
        }
        onClose();
    };

    return (
        <>
            {isOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={onClose}>&times;</span>
                        <h2>{editingProduct ? "Editar Producto" : "Agregar Producto"}</h2> {/* Cambia el título según si se está editando o agregando */}
                        <form onSubmit={handleSubmit}>
                            <label>
                                Número de Parte:
                                <input type="text" name="numeroDeParte" value={productDetails.numeroDeParte} onChange={handleChange} />
                            </label>
                            <label>
                                Nombre:
                                <input type="text" name="nombre" value={productDetails.nombre} onChange={handleChange} />
                            </label>
                            <label>
                                Costo:
                                <input type="number" name="costo" value={productDetails.costo} onChange={handleChange} />
                            </label>
                            <label>
                                Imagen:
                                <input type="file" name="imagen" onChange={handleChange} accept="image/*" />
                            </label>
                            <button type="submit">{editingProduct ? "Guardar" : "Agregar"}</button> {/* Cambia el texto del botón según si se está editando o agregando */}
                        </form>
                    </div>
                </div>
            )}
        </>
    );
};

export default AddProductModal;
